import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import {BrowserRouter as Router, Route, Switch, Link} from 'react-router-dom';
import Drawer from '@mui/material/Drawer';
import Box from '@mui/material/Box';
import { List, ListItem, ListItemText, ListItemIcon, Toolbar, Divider, Typography } from '@material-ui/core';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import Profile from "../pages/Profile"
import Platform from "../pages/Platform"

const drawerWidth = 205;

const useStyles = makeStyles((theme) => ({
    homeMain: {
        flexGrow: 1, 
        p: 3,
    },
    mainbox: {
        display: "flex",
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
        [`& .MuiDrawer-paper`]: { 
            width: drawerWidth, 
            boxSizing: 'border-box',
            backgroundColor: "#E6E6FA",
            backgroundRepeat: "no-repeat",
        } 
    },
    topten: {
        align: "center",
        marginLeft: "20px",
        fontWeight: "bold",
        fontSize: "22px",
    },
}));

export default function Sidebar() {
    const classes = useStyles();
    
    return (
        <Box className={classes.mainbox}>
            <Router>
                <Drawer variant="permanent" className={classes.drawer}>
                    <Toolbar/>
                    <Box>
                        <List>
                            <Link to="/profile">
                                <ListItem button key={"View Profile"}>
                                    <ListItemIcon>
                                        <AccountCircleIcon/>
                                    </ListItemIcon>
                                    <ListItemText>
                                        View Profile
                                    </ListItemText>
                                </ListItem>
                            </Link>
                            <Link to="/platform">
                                <ListItem button key={"Create Platform"}>
                                    <ListItemIcon>
                                        <AddCircleOutlineIcon/>
                                    </ListItemIcon>
                                    <ListItemText>
                                        Create Platform
                                    </ListItemText>
                                </ListItem>
                            </Link>
                        </List>
                        <Divider/>
                        <br/>
                        <Typography className={classes.topten}>Top 10 Sprouts</Typography>
                        <List>
                            {["annie", "judy", "michelle", "steven"].map((text, index)=> (
                                <ListItem>
                                    <ListItemText primary={(index + 1) + "\t" + text}/>
                                </ListItem>
                            ))}
                        </List>
                    </Box>
                </Drawer>
                <Box component="main" classname="homeMain">
                    <Typography paragraph>Quizzes and Platforms</Typography>
                </Box>
                <Switch>
                    <Toolbar/>
                    <Route path="/profile" exact component={Profile}/>
                    <Route path="/platform" exact component={Platform}/>
                </Switch>
            </Router>
        </Box>
    )
}
